import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { Container, Typography, List, ListItem, ListItemText } from '@mui/material';

const AdimaAcilanDofler = () => {
    const [dofs, setDofs] = useState([]);
    const currentUser = "user2"; // Aktif kullanıcı (backend'den alınabilir)
    const navigate = useNavigate();

    useEffect(() => {
        fetch('http://localhost:5000/api/dofs')
            .then((response) => response.json())
            .then((data) => {
                const filteredDofs = data.filter((dof) => dof.assignedTo === currentUser);
                setDofs(filteredDofs);
            })
            .catch((error) => console.error('Error fetching DOFs:', error));
    }, []);

    const handleDofClick = (id) => {
        navigate(`/dof-detay/${id}`);
    };

    return (
        <Container>
            <Typography variant="h4" gutterBottom>
                Adıma Açılan Dofler
            </Typography>
            {dofs.length === 0 ? (
                <Typography variant="body1" color="textSecondary">
                    Şu anda adıma açılmış bir DOF bulunmamaktadır.
                </Typography>
            ) : (
                <List>
                    {dofs.map((dof) => (
                        <ListItem key={dof.id} button onClick={() => handleDofClick(dof.id)}>
                            <ListItemText
                                primary={dof.subject}
                                secondary={`${dof.message} - ${dof.department}`}
                            />
                        </ListItem>
                    ))}
                </List>
            )}
        </Container>
    );
};

export default AdimaAcilanDofler;
