import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { Container, Typography, List, ListItem, ListItemText } from '@mui/material';

const DofListesi = () => {
    const [dofs, setDofs] = useState([]);
    const navigate = useNavigate();

    useEffect(() => {
        fetch('http://localhost:5000/api/dofs')
            .then((response) => response.json())
            .then((data) => setDofs(data))
            .catch((error) => console.error('Error fetching DOFs:', error));
    }, []);

    const handleDofClick = (id) => {
        navigate(`/dof-detay/${id}`);
    };

    return (
        <Container>
            <Typography variant="h4" gutterBottom>
                DÃ–F Listesi
            </Typography>
            <List>
                {dofs.map((dof) => (
                    <ListItem key={dof.id} button onClick={() => handleDofClick(dof.id)}>
                        <ListItemText primary={dof.subject} secondary={`${dof.message} - ${dof.department}`} />
                    </ListItem>
                ))}
            </List>
        </Container>
    );
};

export default DofListesi;
