import React, { useEffect, useState } from 'react';
import { Container, Typography, TextField, MenuItem, List, ListItem, ListItemText } from '@mui/material';

const DofListesi = () => {
    const [dofs, setDofs] = useState([]);
    const [filters, setFilters] = useState({ type: '', channel: '', status: '', startDate: '', endDate: '' });

    useEffect(() => {
        fetch('http://localhost:5000/api/dofs')
            .then((response) => response.json())
            .then((data) => setDofs(data))
            .catch((error) => console.error('Error fetching DOFs:', error));
    }, []);

    const handleFilterChange = (e) => {
        setFilters({ ...filters, [e.target.name]: e.target.value });
    };

    const filteredDofs = dofs.filter((dof) => {
        return (
            (!filters.type || dof.type === filters.type) &&
            (!filters.channel || dof.channel === filters.channel) &&
            (!filters.status || dof.status === filters.status) &&
            (!filters.startDate || new Date(dof.date) >= new Date(filters.startDate)) &&
            (!filters.endDate || new Date(dof.date) <= new Date(filters.endDate))
        );
    });

    return (
        <Container>
            <Typography variant="h4" gutterBottom>
                DÖF Listesi
            </Typography>
            <TextField
                select
                label="DÖF Tipi"
                name="type"
                value={filters.type}
                onChange={handleFilterChange}
                fullWidth
                margin="normal"
            >
                <MenuItem value="">Tümü</MenuItem>
                <MenuItem value="Düzeltici Faaliyet">Düzeltici Faaliyet</MenuItem>
                <MenuItem value="Önleyici Faaliyet">Önleyici Faaliyet</MenuItem>
                <MenuItem value="İyileştirici Faaliyet">İyileştirici Faaliyet</MenuItem>
            </TextField>
            <TextField
                select
                label="DÖF Kanalı"
                name="channel"
                value={filters.channel}
                onChange={handleFilterChange}
                fullWidth
                margin="normal"
            >
                <MenuItem value="">Tümü</MenuItem>
                <MenuItem value="İç Tetkik Sonuçları">İç Tetkik Sonuçları</MenuItem>
                <MenuItem value="Dış Tetkik Sonuçları">Dış Tetkik Sonuçları</MenuItem>
            </TextField>
            <TextField
                label="Başlangıç Tarihi"
                name="startDate"
                type="date"
                value={filters.startDate}
                onChange={handleFilterChange}
                fullWidth
                margin="normal"
                InputLabelProps={{ shrink: true }}
            />
            <TextField
                label="Bitiş Tarihi"
                name="endDate"
                type="date"
                value={filters.endDate}
                onChange={handleFilterChange}
                fullWidth
                margin="normal"
                InputLabelProps={{ shrink: true }}
            />
            <List>
                {filteredDofs.map((dof) => (
                    <ListItem key={dof.id}>
                        <ListItemText primary={dof.subject} secondary={`${dof.message} - ${dof.department}`} />
                    </ListItem>
                ))}
            </List>
        </Container>
    );
};

export default DofListesi;
