import React, { useEffect, useState } from 'react';
import { Container, Typography, List, ListItem, ListItemText, Button, TextField } from '@mui/material';

const DofKontrol = () => {
    const [dofs, setDofs] = useState([]);
    const [selectedDof, setSelectedDof] = useState(null);
    const [comment, setComment] = useState('');

    useEffect(() => {
        fetch('http://localhost:5000/api/dofs')
            .then((response) => response.json())
            .then((data) => setDofs(data))
            .catch((error) => console.error('Error fetching DOFs:', error));
    }, []);

    const handleSelectDof = (dof) => {
        setSelectedDof(dof);
    };

    const handleApprove = () => {
        fetch(`http://localhost:5000/api/dofs/${selectedDof.id}/approve`, {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({ comment }),
        })
            .then((response) => response.json())
            .then(() => {
                alert('DÖF onaylandı ve süreç başlatıldı.');
                setSelectedDof(null);
                setComment('');
            })
            .catch((error) => console.error('Error approving DOF:', error));
    };

    const handleReject = () => {
        fetch(`http://localhost:5000/api/dofs/${selectedDof.id}/reject`, {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({ comment }),
        })
            .then((response) => response.json())
            .then(() => {
                alert('DÖF reddedildi.');
                setSelectedDof(null);
                setComment('');
            })
            .catch((error) => console.error('Error rejecting DOF:', error));
    };

    return (
        <Container>
            <Typography variant="h4" gutterBottom>
                Kalite Departmanı Kontrol Paneli
            </Typography>
            {selectedDof ? (
                <div>
                    <Typography variant="h6">Seçilen DÖF: {selectedDof.subject}</Typography>
                    <TextField
                        label="Açıklama"
                        value={comment}
                        onChange={(e) => setComment(e.target.value)}
                        fullWidth
                        margin="normal"
                    />
                    <Button variant="contained" color="primary" onClick={handleApprove}>
                        Onayla ve Süreci Başlat
                    </Button>
                    <Button variant="contained" color="secondary" onClick={handleReject}>
                        Reddet (DÖF Değil)
                    </Button>
                </div>
            ) : (
                <List>
                    {dofs.map((dof) => (
                        <ListItem key={dof.id} button onClick={() => handleSelectDof(dof)}>
                            <ListItemText primary={dof.subject} secondary={dof.message} />
                        </ListItem>
                    ))}
                </List>
            )}
        </Container>
    );
};

export default DofKontrol;
