import React from 'react';
import { AppBar, Toolbar, Typography, Box } from '@mui/material';

const Header = ({ currentUser }) => {
    return (
        <AppBar
            position="static"
            sx={{
                backgroundColor: 'white', // Modern mavi ton
                color: '#ffffff', // Beyaz yazı
                boxShadow: '0px 4px 6px rgba(0, 0, 0, 0.1)', // Hafif gölge
            }}
        >
            <Toolbar>
                <Typography
                    variant="h6"
                    sx={{
                        flexGrow: 1,
                        fontWeight: 'bold',
                        fontFamily: 'Roboto, sans-serif',
                    }}
                >
                    DÖF Yönetim Sistemi
                </Typography>
                {currentUser && (
                    <Box
                        sx={{
                            backgroundColor: '#ffffff', // Beyaz arka plan
                            color: '#1976d2', // Mavi yazı
                            padding: '5px 15px',
                            borderRadius: '20px',
                            fontWeight: 'bold',
                            fontSize: '0.9rem',
                        }}
                    >
                        Hoşgeldiniz, {currentUser}
                    </Box>
                )}
            </Toolbar>
        </AppBar>
    );
};

export default Header;
