import React, { useEffect, useState } from 'react';
import { Container, Typography, Table, TableBody, TableCell, TableHead, TableRow, Paper, Button } from '@mui/material';

const DofKontrol = () => {
    const [dofs, setDofs] = useState([]);

    useEffect(() => {
        fetch('http://localhost:5000/api/dofs')
            .then((response) => response.json())
            .then((data) => setDofs(data))
            .catch((error) => console.error('Error fetching DOFs:', error));
    }, []);

    return (
        <Container>
            <Typography variant="h4" gutterBottom>
                DÖF Kontrol Paneli
            </Typography>
            <Paper elevation={3} sx={{ overflow: 'hidden' }}>
                <Table>
                    <TableHead>
                        <TableRow sx={{ backgroundColor: '#f5f5f5' }}>
                            <TableCell><strong>DÖF Adı</strong></TableCell>
                            <TableCell><strong>Mesaj</strong></TableCell>
                            <TableCell><strong>Tarih</strong></TableCell>
                            <TableCell><strong>Durum</strong></TableCell>
                            <TableCell><strong>İşlemler</strong></TableCell>
                        </TableRow>
                    </TableHead>
                    <TableBody>
                        {dofs.map((dof) => (
                            <TableRow
                                key={dof.id}
                                sx={{
                                    '&:hover': {
                                        backgroundColor: '#f0f8ff',
                                        cursor: 'pointer',
                                    },
                                }}
                            >
                                <TableCell>{dof.subject}</TableCell>
                                <TableCell>{dof.message}</TableCell>
                                <TableCell>{new Date(dof.created_at).toLocaleDateString()}</TableCell>
                                <TableCell>{dof.status}</TableCell>
                                <TableCell>
                                    <Button size="small" color="primary">Onayla</Button>
                                    <Button size="small" color="secondary">Reddet</Button>
                                </TableCell>
                            </TableRow>
                        ))}
                    </TableBody>
                </Table>
            </Paper>
        </Container>
    );
};

export default DofKontrol;
