import React, { useState } from 'react';
import { AppBar, Toolbar, Typography, Box, IconButton, InputBase } from '@mui/material';
import MenuIcon from '@mui/icons-material/Menu';
import NotificationsIcon from '@mui/icons-material/Notifications';
import SearchIcon from '@mui/icons-material/Search';

const Header = ({ currentUser, toggleSidebar }) => {
    const [searchQuery, setSearchQuery] = useState('');

    const handleSearch = () => {
        console.log('Arama yapılıyor:', searchQuery);
        // Arama işlemini burada gerçekleştirin
    };

    return (
        <AppBar
            position="static"
            sx={{
                backgroundColor: '#1976d2', // Modern mavi ton
                color: '#ffffff', // Beyaz yazı
                boxShadow: '0px 4px 6px rgba(0, 0, 0, 0.1)', // Hafif gölge
            }}
        >
            <Toolbar>
                <IconButton
                    edge="start"
                    color="inherit"
                    aria-label="menu"
                    onClick={toggleSidebar}
                    sx={{ marginRight: '20px' }}
                >
                    <MenuIcon />
                </IconButton>
                <Typography
                    variant="h6"
                    sx={{
                        flexGrow: 1,
                        fontWeight: 'bold',
                        fontFamily: 'Roboto, sans-serif',
                    }}
                >
                    DÖF Yönetim Sistemi
                </Typography>
                <Box
                    sx={{
                        display: 'flex',
                        alignItems: 'center',
                        backgroundColor: '#ffffff',
                        borderRadius: '20px',
                        padding: '5px 10px',
                        marginRight: '20px',
                    }}
                >
                    <SearchIcon sx={{ color: '#1976d2', marginRight: '5px' }} />
                    <InputBase
                        placeholder="Ara..."
                        value={searchQuery}
                        onChange={(e) => setSearchQuery(e.target.value)}
                        onKeyPress={(e) => e.key === 'Enter' && handleSearch()}
                        sx={{ color: '#1976d2', width: '200px' }}
                    />
                </Box>
                {currentUser && (
                    <Box
                        sx={{
                            backgroundColor: '#ffffff', // Beyaz arka plan
                            color: '#1976d2', // Mavi yazı
                            padding: '5px 15px',
                            borderRadius: '20px',
                            fontWeight: 'bold',
                            fontSize: '0.9rem',
                            marginRight: '20px',
                        }}
                    >
                        Hoşgeldiniz, {currentUser}
                    </Box>
                )}
                <IconButton color="inherit">
                    <NotificationsIcon />
                </IconButton>
            </Toolbar>
        </AppBar>
    );
};

export default Header;
