import React, { useEffect, useState } from 'react';
import { Container, Typography, List, ListItem, ListItemText, Button, Card, CardContent, CardActions } from '@mui/material';

const ActigimDofler = () => {
    const [dofs, setDofs] = useState([]);
    const currentUser = "user2"; // Aktif kullanıcı (backend'den alınabilir)

    useEffect(() => {
        // Sadece currentUser tarafından oluşturulan DOF'leri getir
        fetch(`http://localhost:5000/api/dofs?createdBy=${currentUser}`)
            .then((response) => response.json())
            .then((data) => setDofs(data))
            .catch((error) => console.error('Error fetching DOFs:', error));
    }, []);

    const handleEdit = (id) => {
        console.log(`Düzenle: ${id}`);
        // Düzenleme işlemi için yönlendirme yapılabilir
    };

    const handleDelete = (id) => {
        console.log(`Sil: ${id}`);
        // Silme işlemi için API çağrısı yapılabilir
    };

    return (
        <Container>
            <Typography variant="h4" gutterBottom>
                Açtığım Dofler
            </Typography>
            {dofs.length === 0 ? (
                <Typography variant="body1" color="textSecondary">
                    Şu anda açtığınız bir DOF bulunmamaktadır.
                </Typography>
            ) : (
                <List>
                    {dofs.map((dof) => (
                        <Card key={dof.id} style={{ marginBottom: '16px' }}>
                            <CardContent>
                                <Typography variant="h6">{dof.subject}</Typography>
                                <Typography variant="body2" color="textSecondary">
                                    {dof.message} - {dof.department}
                                </Typography>
                            </CardContent>
                            <CardActions>
                                <Button size="small" color="primary" onClick={() => handleEdit(dof.id)}>
                                    Düzenle
                                </Button>
                                <Button size="small" color="secondary" onClick={() => handleDelete(dof.id)}>
                                    Sil
                                </Button>
                            </CardActions>
                        </Card>
                    ))}
                </List>
            )}
        </Container>
    );
};

export default ActigimDofler;
