import React, { useEffect, useState } from 'react';
import { Container, Typography, List, ListItem, ListItemText } from '@mui/material';

const AdimaAcilanDofler = () => {
    const [dofs, setDofs] = useState([]);
    const currentUser = "user2"; // Aktif kullanıcı (backend'den alınabilir)

    useEffect(() => {
        // API'den sadece adıma açılan DOF'leri getir
        fetch(`http://localhost:5000/api/dofs?assignedTo=${currentUser}`)
            .then((response) => response.json())
            .then((data) => setDofs(data))
            .catch((error) => console.error('Error fetching DOFs:', error));
    }, []);

    return (
        <Container>
            <Typography variant="h4" gutterBottom>
                Adıma Açılan Dofler
            </Typography>
            {dofs.length === 0 ? (
                <Typography variant="body1" color="textSecondary">
                    Şu anda adıma açılmış bir DOF bulunmamaktadır.
                </Typography>
            ) : (
                <List>
                    {dofs.map((dof) => (
                        <ListItem key={dof.id}>
                            <ListItemText
                                primary={dof.subject}
                                secondary={`${dof.message} - ${dof.department}`}
                            />
                        </ListItem>
                    ))}
                </List>
            )}
        </Container>
    );
};

export default AdimaAcilanDofler;
