import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Sidebar from './components/Sidebar';
import Header from './components/Header';
import AdimaAcilanDofler from './components/AdimaAcilanDofler';
import DofAc from './components/DofAc';
import RaporCalistir from './components/RaporCalistir';
import ActigimDofler from './components/ActigimDofler';
import DofKontrol from './components/DofKontrol';

const App = () => {
    const [isLoggedIn, setIsLoggedIn] = useState(true);
    const [isSidebarOpen, setIsSidebarOpen] = useState(false);

    const toggleSidebar = () => {
        setIsSidebarOpen(!isSidebarOpen);
    };

    return (
        <Router>
            <div style={{ display: 'flex', flexDirection: 'column', minHeight: '100vh' }}>
                {isLoggedIn && <Header toggleSidebar={toggleSidebar} />}
                <Sidebar isOpen={isSidebarOpen} toggleSidebar={toggleSidebar} />
                <div style={{ marginLeft: isLoggedIn ? 240 : 0, padding: '20px', flex: 1 }}>
                    <Routes>
                        <Route path="/actigim-dofler" element={<ActigimDofler />} />
                        <Route path="/dof-ac" element={<DofAc />} />
                        <Route path="/rapor-calistir" element={<RaporCalistir />} />
                        <Route path="/dof-kontrol" element={<DofKontrol />} />
                        <Route path="/" element={<Navigate to="/actigim-dofler" />} />
                    </Routes>
                </div>
            </div>
        </Router>
    );
};

export default App;
