import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Sidebar from './components/Sidebar';
import AdimaAcilanDofler from './components/AdimaAcilanDofler';
import DofAc from './components/DofAc';
import RaporCalistir from './components/RaporCalistir';
import Login from './components/Login';
import ActigimDofler from './components/ActigimDofler';
import DofListesi from './components/DofListesi';
import DofDetay from './components/DofDetay';
import DofKontrol from './components/DofKontrol';
import Header from './components/Header';
import Footer from './components/Footer';

const App = () => {
    const [isLoggedIn, setIsLoggedIn] = useState(false);
    const [currentUser, setCurrentUser] = useState(null); // Kullanıcı bilgisi

    return (
        <Router>
            <div style={{ display: 'flex', flexDirection: 'column', minHeight: '100vh' }}>
                {isLoggedIn && <Header currentUser={currentUser} />}
                <div style={{ display: 'flex' }}>
                    {isLoggedIn && <Sidebar setIsLoggedIn={setIsLoggedIn} />}
                    <div style={{ marginLeft: isLoggedIn ? 240 : 0, padding: '20px', flex: 1 }}>
                        <Routes>
                            <Route
                                path="/login"
                                element={<Login setIsLoggedIn={setIsLoggedIn} />}
                            />
                            <Route
                                path="/"
                                element={isLoggedIn ? <AdimaAcilanDofler /> : <Navigate to="/login" />}
                            />
                            <Route
                                path="/dof-ac"
                                element={isLoggedIn ? <DofAc /> : <Navigate to="/login" />}
                            />
                            <Route
                                path="/rapor-calistir"
                                element={isLoggedIn ? <RaporCalistir /> : <Navigate to="/login" />}
                            />
                            <Route
                                path="/adima-acilan-dofler"
                                element={isLoggedIn ? <AdimaAcilanDofler /> : <Navigate to="/login" />}
                            />
                            <Route
                                path="/actigim-dofler"
                                element={isLoggedIn ? <ActigimDofler /> : <Navigate to="/login" />}
                            />
                            <Route
                                path="/dof-listesi"
                                element={isLoggedIn ? <DofListesi /> : <Navigate to="/login" />}
                            />
                            <Route
                                path="/dof-detay/:id"
                                element={isLoggedIn ? <DofDetay /> : <Navigate to="/login" />}
                            />
                            <Route
                                path="/dof-kontrol"
                                element={isLoggedIn ? <DofKontrol /> : <Navigate to="/login" />}
                            />
                        </Routes>
                    </div>
                </div>
                <Footer />
            </div>
        </Router>
    );
};

export default App;
