import React, { useEffect, useState } from 'react';
import { Container, Typography, TextField, MenuItem, List, ListItem, ListItemText } from '@mui/material';

const DofListesi = () => {
    const [dofs, setDofs] = useState([]);
    const [filter, setFilter] = useState('');
    const [filteredDofs, setFilteredDofs] = useState([]);

    useEffect(() => {
        fetch('http://localhost:5000/api/dofs')
            .then((response) => response.json())
            .then((data) => setDofs(data))
            .catch((error) => console.error('Error fetching DOFs:', error));
    }, []);

    useEffect(() => {
        if (filter) {
            setFilteredDofs(dofs.filter((dof) => dof.type === filter));
        } else {
            setFilteredDofs(dofs);
        }
    }, [filter, dofs]);

    return (
        <Container>
            <Typography variant="h4" gutterBottom>
                DÖF Listesi
            </Typography>
            <TextField
                select
                label="DÖF Tipine Göre Filtrele"
                value={filter}
                onChange={(e) => setFilter(e.target.value)}
                fullWidth
                margin="normal"
            >
                <MenuItem value="">Tümü</MenuItem>
                <MenuItem value="Düzeltici Faaliyet">Düzeltici Faaliyet</MenuItem>
                <MenuItem value="Önleyici Faaliyet">Önleyici Faaliyet</MenuItem>
                <MenuItem value="İyileştirici Faaliyet">İyileştirici Faaliyet</MenuItem>
                <MenuItem value="Ramak Kala Bildirimleri">Ramak Kala Bildirimleri</MenuItem>
                <MenuItem value="Kaza Bildirimleri">Kaza Bildirimleri</MenuItem>
                <MenuItem value="Uygunsuz Durum - Davranış Bildirimleri">Uygunsuz Durum - Davranış Bildirimleri</MenuItem>
            </TextField>
            <List>
                {filteredDofs.map((dof) => (
                    <ListItem key={dof.id}>
                        <ListItemText
                            primary={dof.subject}
                            secondary={`${dof.message} - ${dof.department}`}
                        />
                    </ListItem>
                ))}
            </List>
        </Container>
    );
};

export default DofListesi;
